function select(){let e=document.querySelectorAll(".select");if(!e)return null;e.forEach((e=>{const t=e.querySelector(".select__selected"),s=e.querySelector(".select__tokens");t&&s&&t.addEventListener("click",(()=>{e.classList.toggle("open"),e.classList.contains("open")&&adjustTokenListHeight(e,s)}))}))}function adjustTokenListHeight(e,t){const s=e.getBoundingClientRect(),n=window.innerHeight-s.bottom- -40;t.style.maxHeight=`${n}px`,t.style.overflowY="auto"}select(),window.addEventListener("resize",(()=>{document.querySelectorAll(".select.open .select__tokens").forEach((e=>{adjustTokenListHeight(e.closest(".select"),e)}))}));